	deleteContact = (id) => {
		const { Contacts } = this.state;
		const newContacts = Contacts.filter((contact) => contact.id !== id);
		this.setState({
			Contacts: newContacts
		});
	};

	render() {
		return (
			<Consumer>
				{(value) => {
					const { Contacts } = value;
					return (
						<React.Fragment>
							{Contacts.map((contact) => (
								<div key={contact.id}>
									<Contact
										contact={contact}
										deleteClickHandler={this.deleteContact.bind(this, contact.id)}
									/>
								</div>
							))}
						</React.Fragment>
					);
				}}
